import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [pokemonList, setPokemonList] = useState([]);

  useEffect(() => {
    axios.get("https://pokeapi.co/api/v2/pokemon?limit=20")
      .then((response) => setPokemonList(response.data.results))
      .catch((error) => console.error("Error fetching Pokémon:", error));
  }, []);

  return (
    <div className="home">
      <h1>Pokémon Cards</h1>
      <div className="card-container">
        {pokemonList.map((pokemon, index) => (
          <div key={index} className="card">
            <div className="pkmn__container">
              <div className="pkmn__picture">
                <div className="pkmn__cp">#{index + 1}</div>
                <img
                  className="pkmn__png object-fill h-28"
                  src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${index + 1}.svg`}
                  alt={pokemon.name}
                />
              </div>
              <div className="pkmn__info">
                <div className="pkmn__name">
                  {pokemon.name}
                  <p className="size-medium">Type: TBD</p>
                </div>
                <div className="pkmn__data">
                  <div className="pkmn__type">
                    TBD
                    <p className="text--small">Attack</p>
                  </div>
                  <div className="pkmn__weight">
                    TBD
                    <p className="text--small">Defense</p>
                  </div>
                  <div className="pkmn__type">
                    TBD
                    <p className="text--small">Speed</p>
                  </div>
                  <div className="pkmn__height">
                    TBD
                    <p className="text--small">HP</p>
                  </div>
                </div>
              </div>
              <Link to={`/pokemon/${index + 1}`}>
                <button className="details-button">View Details</button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
