import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Game.css";

const Game = () => {
  const [userPokemon, setUserPokemon] = useState(null);
  const [opponentPokemon, setOpponentPokemon] = useState(null);
  const [selectedStat, setSelectedStat] = useState("");
  const [winner, setWinner] = useState(null);
  const [pokemonList, setPokemonList] = useState([]);

  useEffect(() => {
    const fetchPokemonList = async () => {
      const response = await axios.get("https://pokeapi.co/api/v2/pokemon?limit=20");
      setPokemonList(response.data.results);
    };

    fetchPokemonList();
  }, []);

  const fetchOpponentPokemon = async () => {
    const randomId = Math.floor(Math.random() * 150) + 1; // Random ID between 1 and 150
    const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${randomId}`);
    setOpponentPokemon(response.data);
  };

  const handleBattle = () => {
    if (!userPokemon || !opponentPokemon || !selectedStat) return;

    const userStat = userPokemon.stats.find(stat => stat.stat.name === selectedStat)?.base_stat || 0;
    const opponentStat = opponentPokemon.stats.find(stat => stat.stat.name === selectedStat)?.base_stat || 0;

    if (userStat > opponentStat) {
      setWinner("User");
    } else if (opponentStat > userStat) {
      setWinner("Opponent");
    } else {
      setWinner("Draw");
    }
  };

  return (
    <div className="game">
      <h1>Pokémon Battle</h1>
      <div className="selection-container">
        <h2>Select Your Pokémon</h2>
        <div className="pokemon-list">
          {pokemonList.map((pokemon, index) => (
            <div
              key={index}
              className={`pokemon-card ${userPokemon?.name === pokemon.name ? "selected" : ""}`}
              onClick={async () => {
                const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${index + 1}`);
                setUserPokemon(response.data);
                setOpponentPokemon(null);
                setWinner(null);
              }}
            >
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index + 1}.png`}
                alt={pokemon.name}
              />
              <p>{pokemon.name}</p>
            </div>
          ))}
        </div>
      </div>

      {userPokemon && (
        <div className="battle-container">
          <h2>Your Pokémon</h2>
          <div className="pokemon-card">
            <img
              src={userPokemon.sprites.other["dream_world"].front_default || userPokemon.sprites.front_default}
              alt={userPokemon.name}
            />
            <p>{userPokemon.name}</p>
          </div>
          <h2>Select a Stat for Battle</h2>
          <div className="stat-selection">
            {userPokemon.stats.map(stat => (
              <button
                key={stat.stat.name}
                className={`stat-button ${selectedStat === stat.stat.name ? "selected" : ""}`}
                onClick={() => setSelectedStat(stat.stat.name)}
              >
                {stat.stat.name} ({stat.base_stat})
              </button>
            ))}
          </div>
          <button className="battle-button" onClick={fetchOpponentPokemon}>
            Find Opponent
          </button>
        </div>
      )}

      {opponentPokemon && (
        <div className="opponent-container">
          <h2>Opponent Pokémon</h2>
          <div className="pokemon-card">
            <img
              src={opponentPokemon.sprites.other["dream_world"].front_default || opponentPokemon.sprites.front_default}
              alt={opponentPokemon.name}
            />
            <p>{opponentPokemon.name}</p>
          </div>
          <button className="battle-button" onClick={handleBattle}>
            Battle!
          </button>
        </div>
      )}

      {winner && (
        <div className="winner">
          {winner === "Draw" ? <h2>It's a Draw!</h2> : <h2>Winner: {winner}</h2>}
        </div>
      )}
    </div>
  );
};

export default Game;
