import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Card = () => {
  const { id } = useParams();
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon/${id}`)
      .then((response) => setPokemon(response.data))
      .catch((error) =>
        console.error("Error fetching Pokémon details:", error)
      );
  }, [id]);

  if (!pokemon) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="card">
        <div className="pkmn__container">
          <div className="pkmn__picture">
            <div className="pkmn__cp">#{pokemon.id}</div>
            <img
              className="pkmn__png object-fill h-28"
              src={pokemon.sprites.other["dream_world"].front_default}
              alt={pokemon.name}
            />
          </div>
          <div className="pkmn__info">
            <div className="pkmn__name">{pokemon.name}</div>
            <div className="pkmn__data">
              <div className="pkmn__type">
                Attack: {pokemon.stats[1].base_stat}
              </div>
              <div className="pkmn__weight">
                Defense: {pokemon.stats[2].base_stat}
              </div>
              <div className="pkmn__type">
                Speed: {pokemon.stats[5].base_stat}
              </div>
              <div className="pkmn__height">
                HP: {pokemon.stats[0].base_stat}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
